import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory, useRouteMatch, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getAllUsers, deleteUser, getUser } from "../actions/users";
import { getAllRoles } from "../actions/roles";
import { logoutUser } from "../actions/auth";
const Userprofile = () => {
  const [userList, setUsersList] = useState([]);
  const history = useHistory(); // instance au navigation d'utilisateur
  const match = useRouteMatch(); // permet d'extraire le lien courant
  const dispatcher = useDispatch(); // permet d'exectuer les actions de reducer pour modifier l'etat
  const state = useSelector((state) => state); // permet d'acces à tous l'etat( données ) depuis n'importe quel emplacement
  useEffect(() => {
    // useEffect permet d'execctuer une tache lors de l'initialisation de l'interface
    dispatcher(getUser(state._id));
    console.log(state.userReducer); //appel
    console.log("All State", state);

    //setUsersList(state.userReducer);
  }, []);

  return (
    <div>
      <link rel="stylesheet" href="css/style.css" />
      <title>Welcome To </title>
      <nav className="navbar bg-dark">
        <h1>
          <a href="index.html">
            <i className="fas fa-code" /> DevConnector
          </a>
        </h1>
        <ul>
          <li>
            <Link to="addemande">Add Demande</Link>
          </li>
          <li>
            <a href="posts.html">Posts</a>
          </li>
          <li>
            |
            <a href="dashboard.html" title="Dashboard">
              <i className="fas fa-user" />
              <span className="hide-sm">Dashboard</span>
            </a>
          </li>
          <div>
            <div class="hidden md:block"></div>
          </div>
          <div>
            {state.authReducer.auth ? (
              <div>
                <button
                  className="btn btn-primary"
                  onClick={() => {
                    dispatcher(logoutUser());
                    history.replace("/");
                  }}
                >
                  Logout
                </button>
              </div>
            ) : (
              <div>
                {" "}
                <Link to="/login"></Link>
              </div>
            )}
          </div>
        </ul>
      </nav>

      <section className="container">
        <h1 className="large text-primary">My Profile</h1>
        <div class="container">
          <div class="row">
            <div class="col-md-6 img">
              <img
                src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRvzOpl3-kqfNbPcA_u_qEZcSuvu5Je4Ce_FkTMMjxhB-J1wWin-Q"
                alt=""
                class="img-rounded"
              />
            </div>
            <div class="col-md-6 details">
              <blockquote>
                <h5>Fiona Gallagher</h5>
                <small>
                  <cite title="Source Title">
                    Chicago, United States of America{" "}
                    <i class="icon-map-marker"></i>
                  </cite>
                </small>
              </blockquote>
              <p>
                fionagallager@shameless.com <br />
                www.bootsnipp.com <br />
                June 18, 1990
              </p>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Userprofile;
